import React from "react";
import { Container, Row, Col, Image } from "react-bootstrap";

function About() {
  return (
    <section
      id="about"
      style={{
        minHeight: "100vh",
        paddingTop: "80px",
        display: "flex",
        alignItems: "center",
        backgroundColor: "#f8f9fa",
      }}
    >
      <Container>
        <h2 className="mb-4">À propos</h2>
        <Row className="align-items-center">
          <Col md={4}>
            <Image src="/noah.jpg" alt="Noah Sillaire" roundedCircle fluid />
          </Col>
          <Col md={8}>
            <p className="about-description">
              Étudiant en informatique à l’École Hexagone de Clermont-Ferrand,
              je suis passionné par le développement logiciel, la collaboration
              en équipe et les défis techniques. Mes expériences associatives et
              professionnelles m'ont permis de renforcer mes compétences en
              encadrement, en gestion de projet et en développement de jeux
              vidéo, tout en cultivant un fort esprit d'initiative.
            </p>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

export default About;
